const container=document.querySelector(".container"),url="https://randomfox.ca/images/";function getRandomNumber(){return Math.floor(100*Math.random())}function loadImages(numImages=6){let i=0;for(;i<numImages;){const img=document.createElement("img");img.src=`${url}${getRandomNumber()}.jpg`,container.appendChild(img),i++}}loadImages(),window.addEventListener("scroll",()=>{window.scrollY+window.innerHeight>=document.documentElement.scrollHeight&&loadImages()});